      return <Picker.Item key={year} label={year.toString()} value={year} />;
    })}
  </Picker>

  <Picker selectedValue={selectedMonth || (moment().month() + 1)} onValueChange={(val) => setSelectedMonth(val)} style={styles.picker}>
